        });

        BindGrid();
        UpdateAllNeighbours();
    }

    public void GainExperience(MonsterObject mon, int amount)
    {
        if (amount > 0 && (CurrentLevel < Settings.Default.MaxUserLevel || CurrentExperience < MaxExperience))
        {
            int exp = amount;
            int rate = 0;
            if (mon != null)
            {
                var ratio = Compute.CalculateLevelRatio(CurrentLevel, mon.CurrentLevel, Settings.Default.减收益等级差, Settings.Default.收益减少比率);
                exp = (int)Math.Max(0.0, (double)exp - Math.Round((float)exp * ratio));
                exp = (int)((decimal)exp * Settings.Default.MonsterExperienceMultiplier);
                if (CurrentLevel <= Settings.Default.NoobSupportLevel)
                {
                    exp *= 2;
                }
                rate = Math.Min(ExperienceRate, exp);
            }
            int totalexp = exp + rate;
            ExperienceRate -= rate;
            if (totalexp <= 0)
                return;

            int awakeexp = 0;
            if (Character.AwakeningExpEnabled.V)
            {
                awakeexp = (int)((double)totalexp * 0.5);
                if (Character.AwakeningExp.V + awakeexp > Settings.Default.MaxAwakeningExp)
                {
                    awakeexp = Settings.Default.MaxAwakeningExp - Character.AwakeningExp.V;
                    Character.AwakeningExpEnabled.V = false;
                    Enqueue(new 同步补充变量
                    {
                        变量类型 = 1,
                        变量索引 = 50,
                        ObjectID = ObjectID,
                        变量内容 = 3616 + 激活标识
                    });
                }
                totalexp -= awakeexp;
                Character.AwakeningExp.V = Character.AwakeningExp.V + awakeexp;
            }
            if ((CurrentExperience += totalexp) >= MaxExperience && CurrentLevel < Settings.Default.MaxUserLevel)
            {
                while (CurrentExperience >= MaxExperience)
                {
                    CurrentExperience -= MaxExperience;
                    CurrentLevel++;
                }
                LevelChanged();
            }
            Enqueue(new CharacterExperienceChangePacket
            {
                ExperienceGained = totalexp,
                今日增加 = 0,
                经验上限 = 10_000_000,
                ExperienceRate = rate,
                CurrentExperience = CurrentExperience,
                MaxExperience = MaxExperience,
                AwakeningExperienceGained = awakeexp,
                MaxAwakeningExperience = Settings.Default.MaxAwakeningExp
            });
        }
        else if (Character.AwakeningExpEnabled.V)
        {
            if (Character.AwakeningExp.V + amount > Settings.Default.MaxAwakeningExp)
            {
                amount = Settings.Default.MaxAwakeningExp - Character.AwakeningExp.V;
                Character.AwakeningExpEnabled.V = false;
                Enqueue(new 同步补充变量
                {
                    变量类型 = 1,
                    变量索引 = 50,
                    ObjectID = ObjectID,
                    变量内容 = 3616 + 激活标识
                });
            }
            Character.AwakeningExp.V = Character.AwakeningExp.V + amount;
            Enqueue(new CharacterExperienceChangePacket
            {
                ExperienceGained = 0,
                今日增加 = 0,
                经验上限 = 10_000_000,
                ExperienceRate = ExperienceRate,
                CurrentExperience = CurrentExperience,
                MaxExperience = MaxExperience,
                AwakeningExperienceGained = amount,
                MaxAwakeningExperience = Settings.Default.MaxAwakeningExp
            });
        }
    }

    public void GainSkillExperience(ushort skillId)
    {
        if (!Skills.TryGetValue(skillId, out var skill) || CurrentLevel < skill.RequiredLevel)
            return;

        int exp = SEngine.Random.Next(4);
        if (exp > 0)
        {
            if (Equipment.TryGetValue(8, out var v2) && v2.Name == "技巧项链")
                exp += exp;

            if ((skill.Experience.V += (ushort)exp) >= skill.升级经验)
            {
                skill.Experience.V -= (ushort)skill.升级经验;
                skill.Level.V++;
                SendPacket(new SyncSkillLevelPacket
                {
                    SkillID = skill.ID.V,
                    SkillLevel = skill.Level.V
                });
