    public void UserExtendGrid(byte grid, byte newSize)
    {
        if (newSize == 0)
        {
            Connection?.Close(new Exception("Error: The player expanded the backpack.  Error: Expansion parameter error"));
            return;
        }
        if (grid == 1 && InventorySize + newSize > 64)
        {
            Connection?.Close(new Exception("Error: The player expanded the backpack.  Error: The backpack has exceeded its limit."));
            return;
        }
        if (grid == 2 && StorageSize + newSize > 144)
        {
            Connection?.Close(new Exception("Error: The player expanded the backpack.  Error: Warehouse limit exceeded."));
            return;
        }
        if (grid == 7 && ResourcesSize + newSize > 216)
        {
            Connection?.Close(new Exception("Bug: Player expanding resource pack.  Error: The resource pack has exceeded its limit."));
            return;
        }
        switch (grid)
        {
            case 7:
                if (Settings.Default.资源包开关 == 1)
                {
                    int num5 = 10000;
                    int num6 = newSize * num5;
                    if (Gold < num6)
                    {
                        Enqueue(new GameErrorMessagePacket { ErrorCode = 1821 });
                        break;
                    }
                    Gold -= num6;
                    Enqueue(new 同步货币数量
                    {
                        Description = 全部货币描述()
                    });
                    ResourcesSize += newSize;
                    Enqueue(new 背包容量改变
                    {
                        Grid = 7,
                        Capacity = ResourcesSize
                    });
                }
                break;
            case 2:
                {
                    int num3 = Compute.ExtendWarehouse(StorageSize - 16);
                    int num4 = Compute.ExtendWarehouse(StorageSize + newSize - 16) - num3;
                    if (Gold < num4)
                    {
                        Enqueue(new GameErrorMessagePacket { ErrorCode = 1821 });
                        break;
                    }
                    Gold -= num4;
                    Enqueue(new 同步货币数量
                    {
                        Description = 全部货币描述()
                    });
                    StorageSize += newSize;
                    Enqueue(new 背包容量改变
                    {
                        Grid = 2,
                        Capacity = StorageSize
                    });
                    break;
                }
            case 1:
                {
                    int num = Compute.ExtendInventory(InventorySize - 32);
                    int num2 = Compute.ExtendInventory(InventorySize + newSize - 32) - num;
                    if (Gold < num2)
                    {
                        Enqueue(new GameErrorMessagePacket { ErrorCode = 1821 });
                        break;
                    }
                    Gold -= num2;
                    Enqueue(new 同步货币数量
                    {
