        if (source != null && target != null && source.CanStack && target.ID == source.ID && source.StackSize > source.Dura.V && target.StackSize > target.Dura.V)
        {
            int num = Math.Min(source.Dura.V, target.StackSize - target.Dura.V);
            target.Dura.V += num;
            source.Dura.V -= num;
            Enqueue(new SyncItemPacket
            {
                Description = target.ToArray()
            });
            if (source.Dura.V <= 0)
            {
                source.Remove();
                switch (grid)
                {
                    case 7:
                        Resources.Remove(location);
                        break;
                    case 2:
                        Storage.Remove(location);
                        break;
                    case 1:
                        Inventory.Remove(location);
                        break;
                }
                Enqueue(new DeleteItemPacket
                {
                    Grid = grid,
                    Position = location
                });
            }
            else
            {
                Enqueue(new SyncItemPacket
                {
                    Description = source.ToArray()
                });
            }
            return;
        }
        if (source != null)
        {
            switch (grid)
            {
                case 0:
                    Equipment.Remove(location);
                    break;
                case 1:
                    Inventory.Remove(location);
                    break;
                case 2:
                    Storage.Remove(location);
                    break;
                case 7:
                    Resources.Remove(location);
                    break;
            }
            source.Grid.V = targetGrid;
            source.Location.V = targetLocation;
        }
        if (target != null)
        {
            switch (targetGrid)
            {
                case 0:
                    Equipment.Remove(targetLocation);
                    break;
                case 1:
                    Inventory.Remove(targetLocation);
                    break;
                case 2:
                    Storage.Remove(targetLocation);
                    break;
                case 7:
                    Resources.Remove(location);
                    break;
            }
            target.Grid.V = grid;
            target.Location.V = location;
        }
        if (source != null)
        {
            switch (targetGrid)
            {
                case 0:
                    Equipment[targetLocation] = source as EquipmentInfo;
                    break;
                case 1:
                    Inventory[targetLocation] = source;
                    break;
                case 2:
                    Storage[targetLocation] = source;
                    break;
                case 7:
                    Resources[targetLocation] = source;
                    break;
            }
        }
        if (target != null)
        {
            switch (grid)
            {
                case 0:
                    Equipment[location] = target as EquipmentInfo;
                    break;
                case 1:
                    Inventory[location] = target;
                    break;
                case 2:
                    Storage[location] = target;
                    break;
                case 7:
                    Resources[location] = target;
                    break;
            }
        }
        Enqueue(new 玩家转移物品
        {
            原有容器 = grid,
            目标容器 = targetGrid,
            原有位置 = location,
            目标位置 = targetLocation
        });
        if (targetGrid == 0)
        {
            UserChangeEquipment((EquipmentWearType)targetLocation, (EquipmentInfo)target, (EquipmentInfo)source);
        }
        else if (grid == 0)
        {
            UserChangeEquipment((EquipmentWearType)location, (EquipmentInfo)source, (EquipmentInfo)target);
        }
    }

    public bool AddMount(byte id)
    {
        if (Character.Mounts.Contains(id))
            return false;
        Character.Mounts.Add(id);

        Enqueue(new 同步坐骑数据
        {
            Description = Character.坐骑列表描述()
        });
        return true;
    }

