    public void 高级铭文洗练(byte 装备类型, byte 装备位置, int 物品编号)
    {
        EquipmentInfo 装备数据 = null;
        if (装备类型 == 0 && Equipment.TryGetValue(装备位置, out var v))
        {
            装备数据 = v;
        }
        if (装备类型 == 1 && Inventory.TryGetValue(装备位置, out var v2) && v2 is EquipmentInfo 装备数据2 && Settings.Default.禁止背包铭文洗练 == 0)
        {
            装备数据 = 装备数据2;
        }
