                        v.MaxDura.V -= 3000;
                        v.Dura.V = Math.Min(v.Dura.V, v.MaxDura.V);
                    }
                    else if (num4 < 60)
                    {
                        v.MaxDura.V -= 2000;
                        v.Dura.V = Math.Min(v.Dura.V, v.MaxDura.V);
                    }
                    else if (num4 < 99)
                    {
                        v.MaxDura.V -= 1000;
                        v.Dura.V = Math.Min(v.Dura.V, v.MaxDura.V);
                    }
                    else if (num4 > 130 && Compute.CalculateProbability(1f - (float)v.MaxDura.V * 0.5f / (float)v.DefaultDurability))
                    {
                        v.MaxDura.V += 1000;
                        v.Dura.V = Math.Min(v.Dura.V, v.MaxDura.V);
                    }
                    return;
                }
                Enqueue(new GameErrorMessagePacket
                {
                    ErrorCode = 1859
                });
                return;
            }
            Enqueue(new GameErrorMessagePacket
            {
                ErrorCode = 1859
            });
        }
        else
        {
            Enqueue(new GameErrorMessagePacket
            {
                ErrorCode = 1856
            });
        }
    }

