    public void UserSendBroadcastMessage(uint channel, byte msgtype, byte[] data)
    {
        var message = Encoding.UTF8.GetString(data).Trim('\0');

        switch (channel)
        {
            case 0x90000001u:
                {
                    if (message.StartsWith("@"))
                    {
                        if (SEngine.AddGMCommand(message, CurrentDegree))
                            ;
                        else
                        {
                            message = message.Remove(0, 1);
                            var parts = message.Split(new[] { ' ' }, StringSplitOptions.RemoveEmptyEntries);

                            if (parts.Length == 0) return;


                            switch (parts[0].ToUpper())
                            {
                                case "STALL":
                                    {
                                        if (Character.CurrentMap.V != Settings.Default.可摆摊地图编号)
                                            break;

                                        if (CurrentLevel < Settings.Default.可摆摊等级 && CurrentPrivilege == 0)
                                        {
                                            CurrentTrade?.BreakTrade();
                                            Enqueue(new GameErrorMessagePacket
                                            {
                                                ErrorCode = 65538
                                            });
                                        }
                                        if (!CurrentMap.IsSafeArea(CurrentPosition))
                                        {
                                            Enqueue(new GameErrorMessagePacket
                                            {
                                                ErrorCode = 2818
                                            });
                                        }
                                        else if (CurrentStall != null)
                                        {
                                            Enqueue(new GameErrorMessagePacket
                                            {
                                                ErrorCode = 2825
                                            });
                                        }
                                        else if (CurrentMap.IsSafeArea(CurrentPosition))
                                        {
                                            CurrentStall = new StallObject();
                                            SendPacket(new 摆摊状态改变
                                            {
                                                对象编号 = ObjectID,
                                                摊位状态 = 1
                                            });
                                        }
                                        else
                                        {
                                            Enqueue(new GameErrorMessagePacket
                                            {
                                                ErrorCode = 2818
                                            });
                                        }

                                        break;
                                    }

                                case "会员":
                                    {
                                        if (!Titles.ContainsKey(Settings.Default.分解称号选项))
                                            break;

                                        GuardObject guard = null;
                                        foreach (int id in MapManager.Guards.Keys)
                                        {
                                            if (MapManager.Guards[id].GuardID == 8482)
                                            {
                                                guard = MapManager.Guards[id];
                                                break;
                                            }
                                        }
                                        if (guard != null)
                                        {
                                            guard = new GuardObject(GuardInfo.DataSheet[8482], CurrentMap, GameDirection.Down, new Point(CurrentPosition.X, CurrentPosition.Y + 1));
                                            guard.AutoDisappear = true;
                                            guard.ExistenceTime = SEngine.CurrentTime.AddSeconds(15.0);
                                        }
                                        if (guard != null)
                                        {
                                            GuardSpeak(guard);
                                        }

                                        break;
                                    }
                            }
                        }
                    }
                    else
                    {
                        var buffer = NetworkManager.ComposeMessage(channel, (uint)ObjectID, Name, message, 1, 0);

                        SendPacket(new 接收聊天信息
                        {
                            Description = buffer
                        });

                        SEngine.AddChatLog("[Public][" + Name + "]: ", message);
                    }

                    break;
                }

            case 0x90000003u:
                {
                    switch (msgtype)
                    {
                        case 1:
                            if (Gold < 1000)
                            {
                                Enqueue(new GameErrorMessagePacket
                                {
                                    ErrorCode = 4873
                                });
                                return;
                            }
                            Gold -= 1000;
                            Enqueue(new 同步货币数量
                            {
                                Description = 全部货币描述()
                            });
                            break;
                        case 6:
                            {
                                if (!FindItem(2201, out var item))
                                {
                                    Enqueue(new GameErrorMessagePacket
                                    {
                                        ErrorCode = 4869
                                    });
                                    return;
                                }
                                ConsumeItem(1, item);
                                break;
                            }
                        default:
                            Connection?.Close(new Exception($"Player provided wrong channel parameter when transmitting or broadcasting. Channel: {channel:X8}  Param:{msgtype}"));
                            return;
                    }

                    var buffer = NetworkManager.ComposeMessage((uint)ObjectID, channel, Name, message, msgtype, CurrentLevel);

                    NetworkManager.Broadcast(new SystemMessagePacket
                    {
                        Description = buffer
                    });
                    SEngine.AddChatLog("[" + (msgtype == 1 ? "Broadcast" : "Message") + "][" + Name + "]: ", message);
                    break;
                }
            default:
                Connection?.Close(new Exception($"Player provided wrong channel parameters when sending broadcast. Channel: {channel:X8}"));
                break;
        }
    }

    public void UserSendMessage(ushort param1, uint channel, byte[] data)
    {
        var message = Encoding.UTF8.GetString(data).Trim('\0');

        switch (channel)
        {
            case 0x70000000u:
                {
                    if (Team == null)
                    {
                        Enqueue(new SocialErrorPacket { ErrorCode = 3853 });
                        break;
                    }

                    var buffer = NetworkManager.ComposeMessage((uint)ObjectID, channel, Name, message, 1, CurrentLevel);

                    Team.Broadcast(new SystemMessagePacket
                    {
                        Description = buffer
                    });
                    SEngine.AddChatLog("[Team][" + Name + "]: ", message);
                    break;
                }
            case 0x60000000u:
                {
                    if (Guild == null)
                    {
                        Enqueue(new SocialErrorPacket { ErrorCode = 6668 });
                        break;
                    }
                    if (Guild.BannedMembers.ContainsKey(this.Character))
                    {
                        Enqueue(new SocialErrorPacket { ErrorCode = 4870 });
                        break;
                    }

                    var buffer = NetworkManager.ComposeMessage((uint)ObjectID, channel, Name, message, 1, 0);

                    Guild.Broadcast(new SystemMessagePacket
                    {
                        Description = buffer
                    });
                    SEngine.AddChatLog("[Guild][" + Name + "]: ", message);
                    break;
                }
            case 0:
                {
                    var character = Session.GetCharacter((int)channel);
                    if (character != null)
                    {
                        if (ObjectID == character.ID || Character.BlackList.Contains(Character) || !character.Connected)
                            break;

                        var buffer1 = NetworkManager.ComposeMessage((uint)character.ID, (uint)ObjectID, Name, message, 1, CurrentLevel);
                        var buffer2 = NetworkManager.ComposeMessage((uint)ObjectID, (uint)character.ID, Name, message, 1, 0);
                        
                        Enqueue(new SystemMessagePacket
                        {
                            Description = buffer1
                        });
                        character.Enqueue(new SystemMessagePacket
                        {
                            Description = buffer2
                        });
                        SEngine.AddChatLog($"[Private][{Name}]=>[{character.UserName}]: ", message);
                    }
                    else
                    {
                        Enqueue(new SocialErrorPacket { ErrorCode = 4868 });
                    }
                    break;
                }
        }
    }

