                });
            }
            else if (应答类型 == 2)
            {
                if (MapManager.SandCityStage >= 2 && ((Guild == SystemInfo.Info.OccupyGuild.V && MapManager.SiegeGuilds.Contains(guild)) || (guild == SystemInfo.Info.OccupyGuild.V && MapManager.SiegeGuilds.Contains(Guild))))
                {
