                    Guild.RemoveHostileGuild(guild);
                    NetworkManager.SendAnnouncement($"[{Guild}]解除了和[{guild}]的行会敌对.");
                    Guild.HostileReleaseApplications.Remove(guild);
                }
            }
            else
            {
                Guild.Broadcast(new 解除敌对列表
                {
                    申请类型 = 2,
                    行会编号 = guild.ID
                });
