        {
            PlayerObject value;
            if (CurrentLevel < 30 && CurrentPrivilege == 0)
            {
                CurrentTrade?.BreakTrade();
                Enqueue(new GameErrorMessagePacket
                {
                    ErrorCode = 65538
                });
            }
            else if (对象编号 == ObjectID)
            {
                CurrentTrade?.BreakTrade();
