                });
            }
            else if (CurrentMap != value.CurrentMap)
            {
                CurrentTrade?.BreakTrade();
                Enqueue(new GameErrorMessagePacket
                {
                    ErrorCode = 5636
                });
            }
            else if (GetDistance(value) > 12)
            {
                CurrentTrade?.BreakTrade();
                Enqueue(new GameErrorMessagePacket
                {
