        }
        if (GetDistance(挂机参数[挂机特殊分类.待拾取物品].CurrentPosition) != 0 && 拾取状态)
        {
            Task.Delay(50);
            if (CurrentMap.CanMove(挂机参数[挂机特殊分类.待拾取物品].CurrentPosition))
            {
                拾取时坐标 = CurrentPosition;
                WalkTo(挂机参数[挂机特殊分类.待拾取物品].CurrentPosition);
                if (拾取时坐标 == CurrentPosition)
                {
                    拾取停止次数++;
                }
                else
